Future<BaseNetworkResponse<MoviesResponse>> getMovies(int page) async {

  if (IS_MOCK) {
    return Future.delayed(Duration(milliseconds: 200), () {
      String body = mockMoviesResponses[page];

      MoviesResponse moviesResponse = MoviesResponse.fromJson(json.decode(body));

      return new BaseNetworkResponse<MoviesResponse>(response: moviesResponse);
    });
  }

  DateFormat dateFormat = DateFormat('y-M-d');

  Map<String, String> params = Map.fromEntries([
    MapEntry("api_key", "1f2e9419c5a0c7c4cbe20f4e07fe4859"),
    MapEntry("primary_release_date.gte", dateFormat.format(DateTime.now().add(Duration(days: -9))),),
    MapEntry("primary_release_date.lte", dateFormat.format(DateTime.now().add(Duration(days: 9))),),
    MapEntry("page", page.toString(),),
  ]);

  try {
    http.Response response = await http.get(
        baseUrl + _attachParams('/discover/movie', params),);

    if (response.statusCode != 200) {
      print('getMovies error: ');
      print(response.body);
      ErrorModel error = ErrorModel.fromJson(json.decode(response.body));

      return new BaseNetworkResponse<MoviesResponse>(error: error);
    }

    MoviesResponse moviesResponse = MoviesResponse.fromJson(json.decode(response.body));

    return new BaseNetworkResponse<MoviesResponse>(response: moviesResponse);

  } catch (error) {
    return new BaseNetworkResponse<MoviesResponse>(
        error: ErrorModel(statusCode: -1, statusMessage: 'failed to parse'));
  }
}

