String _createParamString(String key, String value) {
  return key + '=' + value;
}

String _attachParams(String originalRoute, Map<String, String> params) {
  if (params != null) {
    List<String> paramStrings = params
        .map((key, value) => MapEntry(key, _createParamString(key, value)))
        .values.toList();
    for (int i = 0; i < paramStrings.length; i++) {
      originalRoute += i == 0 ? '?' : '&';
      originalRoute += paramStrings[i];
    }
  }
  return originalRoute;
}

